import React, { Component } from 'react';

class Sesion extends Component {
    constructor(props) {
        super(props);
        this.state = {
            trangThai: 0
        }
    }
    thongbao = () => alert("anh yeu em")
    thongbao2 = () => alert("em yêu anh")

    renderButton = () =>
         (
            <div className="bnt bnt-group">
            <button onClick={this.thongbao} className="btn btn-info">edit</button>
            <button onClick={this.thongbao2} className="bnt bnt-warning">remove</button>
        </div>
        )

    renderForm = () => (
        <div className="form-group">
            <input className="form-control" type="text" />
            <div className="btn btn-block btn-danger">Save</div>
        </div>
    )
    displayCheck = () => {
        if(this.state.trangThai === 0){
            return this.renderButton();
        }
    }

    render() {
        return (
            <section>
                <div className="container">
                    <div className="row ">
                        <div className={"col-lg-6 " + this.props.vitri2}>
                            <div className="p-5">
                                <img className="img-fluid rounded-circle" src={"img/" + this.props.img + ".jpg"} alt />
                            </div>
                        </div>
                        <div className={"col-lg-6 " + this.props.vitri1}>
                            <div className="p-5">
                                <h2 className="display-4">Lorem</h2>
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quod aliquid, mollitia odio veniam sit iste esse assumenda amet aperiam exercitationem, ea animi blanditiis recusandae! Ratione voluptatum molestiae adipisci, beatae obcaecati.</p>
                            </div>
                        </div>
                    </div>
                    {this.renderButton}


                </div>
            </section>
        );
    }
}

export default Sesion;
