import React, { Component } from 'react';
import './App.css';
import Content from './Content';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trangthai: "khởi tạo",
      trangthai2: "khởi tạo 2"
    }
  }
  //Mounting
  componentWillMount() {
    console.log('componentWillMount đã chạy')
  }

  componentDidMount() {
    console.log("ComponentDidMount đã chạy");

  }
  //Updation
  shouldComponentUpdate(nextProps, nextState) {
    console.log("shouldComponentUpdate đã chạy");
    return true;
  }

  componentWillUpdate(nextProps, nextState) {
    console.log("componentWillUpdate đã chạy");

  }

  componentDidUpdate(prevProps, prevState) {
    console.log("componentDidUpdate đã chạy");

  }
  updateState = () => {
    this.setState({trangthai: "đã update"})
  }
  render() {
    console.log("Day la ham render");
    console.log('trạng thái là: ' + this.state.trangthai);

    return (
      <div className="App">
        <Content ten={this.state.trangthai2}/>
        <button onClick={this.updateState}>Click vào đây để Update state</button>
      </div>
    );
  }
}

export default App;
