import React, { Component } from 'react';
import data from './dulieu.json';
import NewsRelated from './NewsRelated.js';

class NewsDetail extends Component {

    chuyenDoi = (str) => {
        // Chuyển hết sang chữ thường
        str = str.toLowerCase();     

        // xóa dấu
        str = str.replace(/(à|á|ạ|ả|ã|â|ầ|ấ|ậ|ẩ|ẫ|ă|ằ|ắ|ặ|ẳ|ẵ)/g, 'a');
        str = str.replace(/(è|é|ẹ|ẻ|ẽ|ê|ề|ế|ệ|ể|ễ)/g, 'e');
        str = str.replace(/(ì|í|ị|ỉ|ĩ)/g, 'i');
        str = str.replace(/(ò|ó|ọ|ỏ|õ|ô|ồ|ố|ộ|ổ|ỗ|ơ|ờ|ớ|ợ|ở|ỡ)/g, 'o');
        str = str.replace(/(ù|ú|ụ|ủ|ũ|ư|ừ|ứ|ự|ử|ữ)/g, 'u');
        str = str.replace(/(ỳ|ý|ỵ|ỷ|ỹ)/g, 'y');
        str = str.replace(/(đ)/g, 'd');

        // Xóa ký tự đặc biệt
        str = str.replace(/([^0-9a-z-\s])/g, '');

        // Xóa khoảng trắng thay bằng ký tự -
        str = str.replace(/(\s+)/g, '-');

        // xóa phần dự - ở đầu
        str = str.replace(/^-+/g, '');

        // xóa phần dư - ở cuối
        str = str.replace(/-+$/g, '');

        // return
        return str;
    }

    render() {        
        return (
            <div>
                <div>
                    <header className="masthead tintuc">
                        <div className="container h-100">
                        <div className="row h-100">
                            <div className="col-lg-7 my-auto">
                            <div className="header-content mx-auto">
                                <h1 className="mb-5">Trang danh sách tin</h1>
                            </div>
                            </div>
                        </div>
                        </div>
                    </header>
                    {/* begin tin tức */}
                    {
                        data.map((value, key) => {
                            if (this.props.match.params.id == value.id) {
                                return (
                                    <div className="jumbotron jumbotron-fluid" key={key}>
                                        <div className="container">
                                            <img className="img-fluid anh100" src={value.anh} alt="true" />
                                            <p className="lead">{value.tieuDe}</p>
                                            <hr className="my-2" />
                                            <p>{value.noiDung}</p>
                                        </div>
                                    </div>
                                )
                            }
                        })
                    }
                </div>

                //
                <div className="container">
                    <h4 className="card-title text-center">Tin Liên Quan</h4>
                    <div className="row">
                        <div className="col-12">
                            <div className="card-deck">
                            {
                                data.map((value,key) => {
                                    if (key <= 3) {
                                        return (
                                            <div className="col-3">
                                                <div className="card-deck">
                                                    <div className="card">
                                                        <a href={"./chitiet/"+this.chuyenDoi(value.tieuDe)+"."+value.id+".html"}><img className="card-img-top" src={value.anh} alt="true" /></a>
                                                        <div className="card-body">
                                                            <h4 className="card-title">{value.tieuDe}</h4>
                                                            <p className="card-text">{value.noiDung}</p>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    }
                                })
                            }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default NewsDetail;
