import React, { Component } from 'react';
import data from './dulieu.json';
import NewsRelated from './NewsRelated.js';

class NewsDetail extends Component {
    render() {        
        return (
            <div>
                <div>
                    <header className="masthead tintuc">
                        <div className="container h-100">
                        <div className="row h-100">
                            <div className="col-lg-7 my-auto">
                            <div className="header-content mx-auto">
                                <h1 className="mb-5">Trang danh sách tin</h1>
                            </div>
                            </div>
                        </div>
                        </div>
                    </header>
                    {/* begin tin tức */}
                    {
                        data.map((value, key) => {
                            if (this.props.match.params.id == value.id) {
                                return (
                                    <div className="jumbotron jumbotron-fluid" key={key}>
                                        <div className="container">
                                            <img className="img-fluid anh100" src={value.anh} alt="true" />
                                            <p className="lead">{value.tieuDe}</p>
                                            <hr className="my-2" />
                                            <p>{value.noiDung}</p>
                                        </div>
                                    </div>
                                )
                            }
                        })
                    }
                </div>
                <div className="container">
                    <h4 className="card-title text-center">Tin Liên Quan</h4>
                    <div className="row">
                        <div className="col-12">
                            <div className="card-deck">
                            {
                                data.map((value,key) => {
                                    if (key <= 3) {
                                        return (
                                            // <NewsRelated key={key}
                                            //              anh = {value.anh}
                                            //              tieuDe = {value.tieuDe}
                                            //              noiDung = {value.noiDung}/>
                                            <div className="col-3">
                                                <div className="card-deck">
                                                    <div className="card">
                                                        <a href={"./chitiet/"+chuyenDoi(value.tieuDe)+"."+value.id+".html"}><img className="card-img-top" src={this.props.anh} alt="true" /></a>
                                                        <div className="card-body">
                                                            <h4 className="card-title">{this.props.tieuDe}</h4>
                                                            <p className="card-text">{this.props.noiDung}</p>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    }
                                })
                            }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default NewsDetail;
